mapper.select:
SELECT T_NO, D_STATE, P_NAME, P_PRICE, M_ID, P_CONTENT, P_RDATE, P_UDATE, CG_NAME, P_delete
FROM t_product
WHERE T_NO = #{no}

SELECT no , name , price , rdate , udate , "delete" , userId , siteName , content FROM ( SELECT rownum idx , a.t_no no , a.p_name name , a.p_price price , a.p_rdate rdate , a.p_udate udate , a.p_delete "delete" , a.p_content content , a.m_id userId , c.m_id memberId , c.s_no siteNo , b.s_no sNo , b.s_name siteName FROM t_product a INNER JOIN t_member c on a.m_id = c.m_id INNER JOIN t_site b on c.s_no = b.s_no
WHERE
p_delete = '1' AND rownum < #{ end }
ORDER BY a.t_no DESC ) WHERE idx >= #{ beginning }

mapper.update:
UPDATE t_product 
SET p_delete = '0' 
WHERE t_no = #{ no }

UPDATE t_product
SET p_name = #{ name }, p_price = #{ price }, cg_name = #{ cgName }, p_content = #{ content }, P_UDATE = sysdate
WHERE t_no = #{ no }
